    public void test() {
        WorldChunk chunk = (WorldChunk) cm.getChunk(1,1);
        ArrayList<Integer> soundScene = ((WorldChunkAccessor) chunk).getSoundScene();
    }
    public ArrayList<Integer> fullScene() {
        playerChunk.x = player.getBlockX()>>4;
        playerChunk.y = player.getBlockZ()>>4;
        ArrayList<Integer> data = new ArrayList<>();
        for(int i = playerChunk.x-size; i<playerChunk.x+size; i++) {
            for(int j = playerChunk.y-size; j<playerChunk.y+size; j++) {
                WorldChunk chunk = (WorldChunk) cm.getChunk(i,j);
                if(!(chunk==null)) {
                    data.addAll(((WorldChunkAccessor) chunk).getSoundScene());
                }
                else {
                    data.addAll(emptyChunk);
                }
            }
        }
        return data;
    }
    private int posToIndex(Pos2i p) {
        Pos2i rel = p.sub(playerChunk).add(new Pos2i(size, size));
        return rel.y << 3 | rel.x;
    }

